<?php get_header(); ?><!--WP LOOP--><?php if (have_posts()) : while (have_posts()) : the_post(); ?>	<div class="post">		<div class="post-info">			<span class="post-author">By: <?php the_author(); ?></span>			<span class="post-date"><?php the_time('F j, Y'); ?></span>		</div>		<?php if (has_post_thumbnail()) { ?>			<a class="post-img" href="<?php the_permalink(); ?>">				<?php the_post_thumbnail(); ?>			</a>		<?php } ?>		<h2 class="post-title">			<a href="<?php the_permalink(); ?>"><?php the_title(); ?></a>		</h2>		<div class="excerpt">			<?php the_excerpt(); ?>		</div>	</div><?php endwhile; else: ?><?php endif; ?><!--PAGINATION--><?phpthe_posts_pagination(array(	'before_page_number' => '<span class="meta-nav screen-reader-text">' . __('Page', 'twentyfifteen') . ' </span>',));?><?php get_footer(); ?>